/* Local placeholder stub to avoid editor/linter syntax errors.
   The real qrcode library is loaded from CDN at runtime via ensureQRCodeLib().
   If you need a fully offline copy, I can embed the official minified file here.
*/
(function(){
	if (window.QRCode) return; // don't overwrite if present
	window.QRCode = function(el, opts){
		if (typeof el === 'string') el = document.getElementById(el);
		var container = el || document.body;
		var canvas = document.createElement('canvas');
		canvas.width = (opts && opts.width) || 200;
		canvas.height = (opts && opts.height) || 200;
		var ctx = canvas.getContext && canvas.getContext('2d');
		if (ctx) {
			ctx.fillStyle = (opts && opts.colorLight) || '#ffffff';
			ctx.fillRect(0,0,canvas.width,canvas.height);
			ctx.fillStyle = (opts && opts.colorDark) || '#000000';
			ctx.font = '16px sans-serif';
			ctx.fillText('QR', 10, 20);
		}
		container.appendChild(canvas);
		this.clear = function(){ if (canvas.parentNode) canvas.parentNode.removeChild(canvas); };
		this.makeCode = function(){};
	};
})();